import {SET_ALERT, REMOVE_ALERT} from '../types';

const AlertReducer = (state, action) => {
    switch(action.type){
        case SET_ALERT:
         return action.payload;
        case REMOVE_ALERT:
         return state;
        default:
         return state;
    }
}

export default AlertReducer
